import React, { useEffect, useState } from 'react';
import { Box, Typography, Container, Grid, Card, CardContent, Link, CircularProgress, Button, Collapse } from '@mui/material';

interface Repository {
  name: string;
  description: string;
  html_url: string;
}

const Projects: React.FC = () => {
  const [repositories, setRepositories] = useState<Repository[]>([]);
  const [loading, setLoading] = useState(true);
  const [showDetails, setShowDetails] = useState<{ [key: string]: boolean }>({});

  useEffect(() => {
    fetch('https://api.github.com/users/rizkyngrh23/repos')
      .then(response => response.json())
      .then(data => {
        setRepositories(data);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching repositories:', error);
        setLoading(false);
      });
  }, []);

  const toggleDetails = (name: string) => {
    setShowDetails(prevState => ({
      ...prevState,
      [name]: !prevState[name]
    }));
  };

  return (
    <Container maxWidth="lg" sx={{ py: 4 }}>
      <Box sx={{ textAlign: 'center', mb: 4 }}>
        <Typography variant="h2" component="h1" gutterBottom>
          Projects
        </Typography>
      </Box>
      {loading ? (
        <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '50vh' }}>
          <CircularProgress />
        </Box>
      ) : (
        <Grid container spacing={4}>
          {repositories.map((repo, index) => (
            <Grid item xs={12} sm={6} md={3} key={index}>
              <Card sx={{ height: '100%' }}>
                <CardContent>
                  <Typography variant="h6">{repo.name}</Typography>
                  <Collapse in={showDetails[repo.name]}>
                    <Typography variant="body2">{repo.description}</Typography>
                  </Collapse>
                  <Button
                    onClick={() => toggleDetails(repo.name)}
                    variant="contained"
                    color="primary"
                    sx={{ mt: 1, mb: 1 }}
                  >
                    {showDetails[repo.name] ? 'Hide Details' : 'Show Details'}
                  </Button>
                  <Button
                    href={repo.html_url}
                    target="_blank"
                    variant="outlined"
                    color="secondary"
                    sx={{ mt: 1, mb: 1, ml: 1 }}
                  >
                    View on GitHub
                  </Button>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      )}
      <Box sx={{ textAlign: 'center', mt: 4 }}>
        <Typography variant="body2">&copy; 2024 Rizky Portfolio Website</Typography>
      </Box>
    </Container>
  );
};

export default Projects;
